      onTap: () {
        // Select all text on tap
        Future.delayed(Duration.zero, () {
          final controller = PrimaryScrollController.of(context);
          if (controller != null) {
            controller.selection = TextSelection(
              baseOffset: 0,
              extentOffset: controller.text.length,
